00075         public static T EnsureComponentExists<T>(this GameObject go, bool checkParents = false) where T : MonoBehaviour
00076         {
00077             if (go == null)
00078                 return null;
00079 
00080 
00081             T found = checkParents ? go.GetComponentInParent<T>() : go.GetComponent<T>();
00082             if (found)
00083                 return found;
00084 
00085             return go.AddComponent<T>();
00086         }
