00088         public static void EnsureComponentOnNestedChildren<T>(this GameObject go, bool allowMultiples, bool recurse = false) where T : Component
00089         {
00090             for (int i = 0; i < go.transform.childCount; ++i)
00091             {
00092                 var child = go.transform.GetChild(i);
00093 
00095                 if (child.GetComponent<NetObject>())
00096                     continue;
00097 
00099                 if (recurse & child.childCount > 0)
00100                     EnsureComponentOnNestedChildren<T>(child.gameObject, allowMultiples, recurse);
00101 
00102                 if (!allowMultiples)
00103                     if (child.GetComponent<T>())
00104                         continue;
00105 
00106                 child.gameObject.AddComponent<T>();
00107             }
00108         }
