00110         public static List<Component> reusableComponents = new List<Component>();
00111         public static void DestroyComponentOnNestedChildren<T>(this GameObject go, bool recurse = false) where T : Component
00112         {
00113             for (int i = 0; i < go.transform.childCount; ++i)
00114             {
00115                 var child = go.transform.GetChild(i);
00116 
00117                 if (child.GetComponent<NetObject>())
00118                     continue;
00119 
00121                 if (recurse & child.childCount > 0)
00122                     EnsureComponentOnNestedChildren<T>(child.gameObject, recurse);
00123 
00124                 child.GetComponents(reusableComponents);
00125                 for (int c = reusableComponents.Count - 1; c >= 0; --c)
00126                     if (reusableComponents[c] is T)
00127                         Object.DestroyImmediate(reusableComponents[c]);
00128             }
00129         }
