00131         public static Component AddRigidbody(this GameObject go, Space_XD space)
00132         {
00133             if (space == Space_XD.SPACE_3D)
00134             {
00135                 var rb = go.GetComponent<Rigidbody>();
00136                 if (!rb)
00137                     rb = go.AddComponent<Rigidbody>();
00138 
00139                 rb.isKinematic = true;
00140                 rb.interpolation = RigidbodyInterpolation.Interpolate;
00141                 rb.collisionDetectionMode = CollisionDetectionMode.ContinuousDynamic;
00142 
00143                 return rb;
00144             }
00145             else
00146             {
00147                 var rb = go.GetComponent<Rigidbody2D>();
00148                 if (!rb)
00149                     rb = go.AddComponent<Rigidbody2D>();
00150 
00151                 rb.isKinematic = true;
00152                 rb.interpolation = RigidbodyInterpolation2D.Interpolate;
00153                 rb.collisionDetectionMode = CollisionDetectionMode2D.Continuous;
