00160         public static GameObject CreateNewPrimitiveAsChild(this GameObject go, PrimitiveType primitiveType, ColliderType coltype, 
00161             string name, float scale = 1, AssistColor color = AssistColor.Magenta, params System.Type[] components)
00162         {
00163             var prim = GameObject.CreatePrimitive(primitiveType);
00164 
00165             if (primitiveType == PrimitiveType.Sphere)
00166                 prim.transform.localScale = new Vector3(.5f, .5f, .5f) * scale;
00167             else if (primitiveType == PrimitiveType.Cube)
00168                 prim.transform.localScale = new Vector3(.35f, .35f, .35f) * scale;
00169             else if (primitiveType == PrimitiveType.Capsule)
00170                 prim.transform.localScale = new Vector3(.3f, .3f, .3f) * scale;
00171             else if (primitiveType == PrimitiveType.Cylinder)
00172                 prim.transform.localScale = new Vector3(.51f, .1f, .51f) * scale;
00173 
00174             prim.transform.parent = go.transform;
00175             prim.transform.localPosition = new Vector3(0, 0, 0);
00176             prim.transform.localRotation = new Quaternion(0, 0, 0, 1);
00177             prim.name = name;
00178 
00179             switch (coltype)
00180             {
00181                 case ColliderType.None:
00182                     {
00183                         Object.DestroyImmediate(prim.GetComponent<Collider>());
00184                         break;
00185                     }
00186 
00187                 case ColliderType.Trigger:
00188                     {
00189                         prim.GetComponent<Collider>().isTrigger = true;
00190                         break;
00191                     }
00192                 case ColliderType.Full:
00193                     {
00194                         break;
00195                     }
00196             }
00197 
00198             foreach(var c in components)
00199             {
00200                 prim.AddComponent(c);
00201             }
00202 
00203             prim.GetComponent<Renderer>().material = GetMaterial(color);
00204 
00205             return prim;
00206         }
