00211         private static void PopulateObjStateArrays()
00212         {
00213             var enumnames = System.Enum.GetNames(typeof(ObjStateEditor));
00214             var enumvals = (ObjStateEditor[])System.Enum.GetValues(typeof(ObjStateEditor));
00215             itemStateEnumNames.AddRange(enumnames);
00216             itemStateEnumValues.AddRange(enumvals);
00217 
00218             itemStateEnumNames.Add("Unmounted");
00219             itemStateEnumValues.Add((ObjStateEditor)(-1));
00220         }
