00236             go.AddStatePlaceholder("Mounted", MaskLogic.Operator.AND, ObjState.Visible | ObjState.Mounted, 0, ColliderType.None, PrimitiveType.Cube, AssistColor.Magenta, space, dynamics, scale);
00237             go.AddStatePlaceholder("Unmounted", MaskLogic.Operator.AND, ObjState.Visible | ObjState.Anchored, ObjState.Anchored, ColliderType.Trigger, PrimitiveType.Cylinder, AssistColor.Red, space, dynamics, scale);
00238             go.AddStatePlaceholder("Attached", MaskLogic.Operator.AND, ObjState.Visible | ObjState.Anchored, 0, ColliderType.None, PrimitiveType.Capsule, AssistColor.Green, space, dynamics, scale);
00239             go.AddStatePlaceholder("Transit", MaskLogic.Operator.AND, ObjState.Visible | ObjState.Transit, 0, ColliderType.Full, PrimitiveType.Cube, AssistColor.Yellow, space, dynamics, scale)
