00311         private static GameObject AddStatePlaceholder(this GameObject go, string name, 
00312             ObjStateLogic.Operator op, ObjState mask, ObjState notmask, ColliderType colliderType, PrimitiveType primitiveType,
00313             AssistColor color, Space_XD space, Dynamics dynamics, float scale = 1)
00314         {
00315             string label = name + " Model";
00316 
00317             var visObj = new GameObject(label);
00318             visObj.transform.parent = go.transform;
00319             var toggle = visObj.AddComponent<OnStateChangeToggle>();
00320            
00321             toggle.stateLogic.notMask = (int)notmask;
00322             toggle.stateLogic.operation = op;
00323             toggle.stateLogic.stateMask = (int)mask;
00324 
00325             visObj.CreateNewPrimitiveAsChild(primitiveType, colliderType, label + " Placeholder", scale, color);
00326             return visObj;
00327         }
