00064         private IEnumerator SpawnAsteroid()
00065         {
00066             while (true)
00067             {
00068                 yield return new WaitForSeconds(Random.Range(AsteroidsGame.ASTEROIDS_MIN_SPAWN_TIME, AsteroidsGame.ASTEROIDS_MAX_SPAWN_TIME));
00069 
00070                 Vector2 direction = Random.insideUnitCircle;
00071                 Vector3 position = Vector3.zero;
00072 
00073                 if (Mathf.Abs(direction.x) > Mathf.Abs(direction.y))
00074                 {
00075                     // Make it appear on the left/right side
00076                     position = new Vector3(Mathf.Sign(direction.x) * Camera.main.orthographicSize * Camera.main.aspect, 0, direction.y * Camera.main.orthographicSize);
00077                 }
00078                 else
00079                 {
00080                     // Make it appear on the top/bottom
00081                     position = new Vector3(direction.x * Camera.main.orthographicSize * Camera.main.aspect, 0, Mathf.Sign(direction.y) * Camera.main.orthographicSize);
00082                 }
00083 
00084                 // Offset slightly so we are not out of screen at creation time (as it would destroy the asteroid right away)
00085                 position -= position.normalized * 0.1f;
00086 
00087 
00088                 Vector3 force = -position.normalized * 1000.0f;
00089                 Vector3 torque = Random.insideUnitSphere * Random.Range(500.0f, 1500.0f);
00090                 object[] instantiationData = {force, torque, true};
00091 
00092                 PhotonNetwork.InstantiateRoomObject("BigAsteroid", position, Quaternion.Euler(Random.value * 360.0f, Random.value * 360.0f, Random.value * 360.0f), 0, instantiationData);
00093             }
00094         }
00095 
00096         private IEnumerator EndOfGame(string winner, int score)
00097         {
00098             float timer = 5.0f;
00099 
00100             while (timer > 0.0f)
00101             {
00102                 InfoText.text = string.Format("Player {0} won with {1} points.\n\n\nReturning to login screen in {2} seconds.", winner, score, timer.ToString("n2"));
00103 
00104                 yield return new WaitForEndOfFrame();
00105 
00106                 timer -= Time.deltaTime;
00107             }
00108 
00109             PhotonNetwork.LeaveRoom();
00110         }
