00139         public override void OnPlayerPropertiesUpdate(Player targetPlayer, Hashtable changedProps)
00140         {
00141             if (changedProps.ContainsKey(AsteroidsGame.PLAYER_LIVES))
00142             {
00143                 CheckEndOfGame();
00144                 return;
00145             }
00146 
00147             if (!PhotonNetwork.IsMasterClient)
00148             {
00149                 return;
00150             }
00151 
00152 
00153             // if there was no countdown yet, the master client (this one) waits until everyone loaded the level and sets a timer start
00154             int startTimestamp;
00155             bool startTimeIsSet = CountdownTimer.TryGetStartTime(out startTimestamp);
00156 
00157             if (changedProps.ContainsKey(AsteroidsGame.PLAYER_LOADED_LEVEL))
00158             {
00159                 if (CheckAllPlayerLoadedLevel())
00160                 {
00161                     if (!startTimeIsSet)
00162                     {
00163                         CountdownTimer.SetStartTime();
00164                     }
00165                 }
00166                 else
00167                 {
00168                     // not all players loaded yet. wait:
00169                     Debug.Log("setting text waiting for players! ",this.InfoText);
00170                     InfoText.text = "Waiting for other players...";
00171                 }
00172             }
00173         
00174         }
