00179         // called by OnCountdownTimerIsExpired() when the timer ended
00180         private void StartGame()
00181         {
00182             Debug.Log("StartGame!");
00183 
00184             // on rejoin, we have to figure out if the spaceship exists or not
00185             // if this is a rejoin (the ship is already network instantiated and will be setup via event) we don't need to call PN.Instantiate
00186 
00187             
00188             float angularStart = (360.0f / PhotonNetwork.CurrentRoom.PlayerCount) * PhotonNetwork.LocalPlayer.GetPlayerNumber();
00189             float x = 20.0f * Mathf.Sin(angularStart * Mathf.Deg2Rad);
00190             float z = 20.0f * Mathf.Cos(angularStart * Mathf.Deg2Rad);
00191             Vector3 position = new Vector3(x, 0.0f, z);
00192             Quaternion rotation = Quaternion.Euler(0.0f, angularStart, 0.0f);
00193 
00194             PhotonNetwork.Instantiate("Spaceship", position, rotation, 0);      // avoid this call on rejoin (ship was network instantiated before)
00195 
00196             if (PhotonNetwork.IsMasterClient)
00197             {
00198                 StartCoroutine(SpawnAsteroid());
00199             }
00200         }
