00202         private bool CheckAllPlayerLoadedLevel()
00203         {
00204             foreach (Player p in PhotonNetwork.PlayerList)
00205             {
00206                 object playerLoadedLevel;
00207 
00208                 if (p.CustomProperties.TryGetValue(AsteroidsGame.PLAYER_LOADED_LEVEL, out playerLoadedLevel))
00209                 {
00210                     if ((bool) playerLoadedLevel)
00211                     {
00212                         continue;
00213                     }
00214                 }
00215 
00216                 return false;
00217             }
00218 
00219             return true;
00220         }
00221 
00222         private void CheckEndOfGame()
00223         {
00224             bool allDestroyed = true;
00225 
00226             foreach (Player p in PhotonNetwork.PlayerList)
00227             {
00228                 object lives;
00229                 if (p.CustomProperties.TryGetValue(AsteroidsGame.PLAYER_LIVES, out lives))
00230                 {
00231                     if ((int) lives > 0)
00232                     {
00233                         allDestroyed = false;
00234                         break;
00235                     }
00236                 }
00237             }
00238 
00239             if (allDestroyed)
00240             {
00241                 if (PhotonNetwork.IsMasterClient)
00242                 {
00243                     StopAllCoroutines();
00244                 }
00245 
00246                 string winner = "";
00247                 int score = -1;
00248 
00249                 foreach (Player p in PhotonNetwork.PlayerList)
00250                 {
00251                     if (p.GetScore() > score)
00252                     {
00253                         winner = p.NickName;
00254                         score = p.GetScore();
00255                     }
00256                 }
00257 
00258                 StartCoroutine(EndOfGame(winner, score));
00259             }
00260         }
