00065         private void SwitchAuth(bool isMine)
00066         {
00067 
00068 
00069             for (int i = 0; i < componentToggles.Count; ++i)
00070             {
00071                 var item = componentToggles[i];
00072 
00073                 if (item != null && item.enableIfOwned != EnableIf.Ignore && item.component != null)
00074                 {
00075                     item.component.enabled = (item.enableIfOwned == EnableIf.Owner) ? isMine : !isMine;
00076                 }
00077             }
00078 
00079         }
