00083         private static List<Behaviour> components = new List<Behaviour>();
00084         public virtual void FindUnrecognizedComponents()
00085         {
00087             for (int i = componentLookup.Count - 1; i >= 0; --i)
00088             {
00089                 var comp = componentLookup[i];
00090 
00092                 if (comp == null || (!includeChildren && comp.gameObject != gameObject))
00093                 {
00094                     if (componentLookup.Contains(comp))
00095                     {
00096                         componentLookup.RemoveAt(i);
00097                         componentToggles.RemoveAt(i);
00098                     }
00099                 }
00100             }
00101 
00102 #if PUN_2_OR_NEWER
00103 
00104             if (includeChildren)
00105                 transform.GetNestedComponentsInChildren<Behaviour, NetObject>(components);
00106             else
00107                 GetComponents<Behaviour>(components);
00108 
00109 #endif
00110 
00111             foreach (var comp in components)
00112             {
00113                 if (comp == null)
00114                     continue;
00115 
00116                 var nspace = comp.GetType().Namespace;
00117                 var fullname = comp.GetType().FullName;
00118 
00119                 bool itemize;
00120                 EnableIf defaultEnableIf;
00121 
00122                 if (nspace == null || nspace == "")
00123                 {
00124                     itemize = true;
00125                     defaultEnableIf = EnableIf.Owner;
00126                 }
00127                 else
00128                 {
00129                     defaultEnableIf = EnableIf.Owner;
00130 
00131                     if (comp is AutoOwnerComponentEnable)
00132                     {
00133                         itemize = false;
00134                     }
00135                     else if (comp is NetObject)
00136                     {
00137                         itemize = false;
00138                     }
00139 #if PUN_2_OR_NEWER
00140                     else if (comp is PhotonView)
00141                     {
00142                         itemize = false;
00143                     }
00144 #endif
00145                     else if (nspace.StartsWith("UnityEngine"))
00146                     {
00147                         itemize = includeUnity;
00148                         defaultEnableIf = EnableIf.Ignore;
00149                     }
00150                     else if (nspace.StartsWith("Photon."))
00151                     {
00152                         itemize = includePhoton;
00153                         defaultEnableIf = EnableIf.Ignore;
00154                     }
00155                     else if (nspace.StartsWith("emotitron."))
00156                     {
00157                         itemize = includeSimple;
00158                         defaultEnableIf = EnableIf.Ignore;
00159                     }
00160                     else
00161                     {
00162                         itemize = true;
00163                     }
00164 
00165                 }
00166 
00167                 if (itemize)
00168                 {
00169                     if (!componentLookup.Contains(comp))
00170                     {
00171                         componentToggles.Add(new ComponentToggle() { component = comp, enableIfOwned = defaultEnableIf });
00172                         componentLookup.Add(comp);
00173                     }
00174                 }
00175                 else
00176                 {
00177                     if (componentLookup.Contains(comp))
00178                     {
00179                         componentToggles.RemoveAt(componentLookup.IndexOf(comp));
00180                         componentLookup.Remove(comp);
00181                     }
00182                 }
00183             }
00184         }
00185 #endif
00186                 }
