00127         private void SumKeys(out Vector3 move, out Vector3 turn)
00128         {
00129 
00130             move = new Vector3(0, 0, 0);
00131 
00132             if (Input.touchCount > 0)
00133             {
00134                 var touchpos = Input.GetTouch(0).rawPosition;
00135 
00136                 if (touchpos.x < Screen./*currentResolution.*/width * .333f)
00137                     move.x -= 1;
00138                 else if (touchpos.x > Screen./*currentResolution.*/width * .666f)
00139                     move.x += 1;
00140 
00141                 if (touchpos.y < Screen./*currentResolution.*/height * .333f)
00142                     move.z -= 1;
00143                 else if (touchpos.y > Screen./*currentResolution.*/height * .666f)
00144                     move.z += 1;
00145             }
00146 
00147             //if (Input.GetTouch(0).rawPosition)
00148 
00149             if (Input.GetKey(moveRight))
00150                 move.x += 1;
00151             if (Input.GetKey(moveLeft))
00152                 move.x -= 1;
00153 
00154             if (Input.GetKey(moveUp))
00155                 move.y += 1;
00156             if (Input.GetKey(moveDn))
00157                 move.y -= 1;
00158 
00159             if (Input.GetKey(moveFwd))
00160                 move.z += 1;
00161             if (Input.GetKey(moveBwd))
00162                 move.z -= 1;
00163 
00164             move = Vector3.ClampMagnitude(move, 1);
00165 
00166             turn = new Vector3(0, 0, 0);
00167 
00168             if (Input.GetKey(pitchPos))
00169                 turn.x += 1;
00170             if (Input.GetKey(pitchNeg))
00171                 turn.x -= 1;
00172 
00173             if (Input.GetKey(yawPos))
00174                 turn.y += 1;
00175             if (Input.GetKey(yawNeg))
00176                 turn.y -= 1;
00177 
00178             if (Input.GetKey(rollPos))
00179                 turn.z += 1;
00180             if (Input.GetKey(rollNeg))
00181                 turn.z -= 1;
00182 
00183 
00184 
00185         }
