00187         void Apply()
00188         {
00189 
00190             if (!IsMine)
00191                 return;
00192 
00193             Vector3 move, turn;
00194             SumKeys(out move, out turn);
00195 
00197 
00198             if (rb && !rb.isKinematic)
00199             {
00200 
00203                 if (rb)
00204                     if (clampToCrusher && tc != null)
00205                         rb.MovePosition(tc.PosCrusher.Clamp(rb.position));
00206 
00207                 move *= moveForce * Time.deltaTime;
00208 
00209                 if (moveRelative)
00210                     rb.AddRelativeForce(move, ForceMode.VelocityChange);
00211                 else
00212                     rb.AddForce(move, ForceMode.VelocityChange);
00213 
00214             }
00215 #if !DISABLE_PHYSICS_2D
00216             else if (rb2D && !rb2D.isKinematic)
00217             {
00220                 if (rb2D)
00221                     if (clampToCrusher && tc != null)
00222                         rb2D.MovePosition(tc.PosCrusher.Clamp(rb2D.position));
00223 
00224                 move *= moveForce * Time.deltaTime;
00225 
00226                 if (moveRelative)
00227                     rb2D.AddRelativeForce(move, ForceMode2D.Impulse);
00228                 else
00229                     rb2D.AddForce(move, ForceMode2D.Impulse);
00230             }
00231 #endif
00232             else
00233             {
00234 
00235                 Vector3 pos = (rb) ? rb.position : transform.position;
00236 
00237                 if (moveRelative)
00238                     pos += (transform.localRotation * move) * moveSpeed * Time.deltaTime;
00239                 else
00240                     pos += move * moveSpeed * Time.deltaTime;
00241 
00242                 // If we have a reference to the transform crusher being used for compression, lets contrain our movements to our network limits.
00243                 if (clampToCrusher && tc != null && tc.PosCrusher != null)
00244                     pos = tc.PosCrusher.Clamp(pos);
00245 
00246                 if (rb)
00247                     //rb.position = pos;
00248                     rb.MovePosition(pos);
00249                 else
00250                 {
00251                     transform.position = pos;
00252                 }
00253             }
00254 
00256 
00257             if (rb && !rb.isKinematic)
00258             {
00259                 turn *= turnForce * Time.deltaTime;
00260                 rb.AddRelativeTorque(turn, ForceMode.VelocityChange);
00261             }
00262             else
00263             {
00264 
00265                 if (clampToCrusher && tc != null && tc.RotCrusher.TRSType != TRSType.Quaternion)
00266                 {
00267                     Vector3 v = tc.RotCrusher.Clamp(transform.eulerAngles += turn * turnSpeed * Time.deltaTime);
00268                     transform.localEulerAngles = v;
00269                 }
00270                 else
00271                 {
00272                     transform.rotation *= Quaternion.Euler(turn * turnSpeed * Time.deltaTime);
00273                 }
00274             }
00275         }
00276     }
