00223         private void SendChatMessage(string inputLine)
00224         {
00225             if (string.IsNullOrEmpty(inputLine))
00226             {
00227                 return;
00228             }
00229             if ("test".Equals(inputLine))
00230             {
00231                 if (this.TestLength != this.testBytes.Length)
00232                 {
00233                     this.testBytes = new byte[this.TestLength];
00234                 }
00235 
00236                 this.chatClient.SendPrivateMessage(this.chatClient.AuthValues.UserId, this.testBytes, true);
00237             }
00238 
00239 
00240             bool doingPrivateChat = this.chatClient.PrivateChannels.ContainsKey(this.selectedChannelName);
00241             string privateChatTarget = string.Empty;
00242             if (doingPrivateChat)
00243             {
00244                 // the channel name for a private conversation is (on the client!!) always composed of both user's IDs: "this:remote"
00245                 // so the remote ID is simple to figure out
00246 
00247                 string[] splitNames = this.selectedChannelName.Split(new char[] { ':' });
00248                 privateChatTarget = splitNames[1];
00249             }
00250             //UnityEngine.Debug.Log("selectedChannelName: " + selectedChannelName + " doingPrivateChat: " + doingPrivateChat + " privateChatTarget: " + privateChatTarget);
00251 
00252 
00253             if (inputLine[0].Equals('\\'))
00254             {
00255                 string[] tokens = inputLine.Split(new char[] {' '}, 2);
00256                 if (tokens[0].Equals("\\help"))
00257                 {
00258                     this.PostHelpToCurrentChannel();
00259                 }
00260                 if (tokens[0].Equals("\\state"))
00261                 {
00262                     int newState = 0;
00263 
00264 
00265                     List<string> messages = new List<string>();
00266                     messages.Add ("i am state " + newState);
00267                     string[] subtokens = tokens[1].Split(new char[] {' ', ','});
00268 
00269                     if (subtokens.Length > 0)
00270                     {
00271                         newState = int.Parse(subtokens[0]);
00272                     }
00273 
00274                     if (subtokens.Length > 1)
00275                     {
00276                         messages.Add(subtokens[1]);
00277                     }
00278 
00279                     this.chatClient.SetOnlineStatus(newState,messages.ToArray()); // this is how you set your own state and (any) message
00280                 }
00281                 else if ((tokens[0].Equals("\\subscribe") || tokens[0].Equals("\\s")) && !string.IsNullOrEmpty(tokens[1]))
00282                 {
00283                     this.chatClient.Subscribe(tokens[1].Split(new char[] {' ', ','}));
00284                 }
00285                 else if ((tokens[0].Equals("\\unsubscribe") || tokens[0].Equals("\\u")) && !string.IsNullOrEmpty(tokens[1]))
00286                 {
00287                     this.chatClient.Unsubscribe(tokens[1].Split(new char[] {' ', ','}));
00288                 }
00289                 else if (tokens[0].Equals("\\clear"))
00290                 {
00291                     if (doingPrivateChat)
00292                     {
00293                         this.chatClient.PrivateChannels.Remove(this.selectedChannelName);
00294                     }
00295                     else
00296                     {
00297                         ChatChannel channel;
00298                         if (this.chatClient.TryGetChannel(this.selectedChannelName, doingPrivateChat, out channel))
00299                         {
00300                             channel.ClearMessages();
00301                         }
00302                     }
00303                 }
00304                 else if (tokens[0].Equals("\\msg") && !string.IsNullOrEmpty(tokens[1]))
00305                 {
00306                     string[] subtokens = tokens[1].Split(new char[] {' ', ','}, 2);
00307                     if (subtokens.Length < 2) return;
00308 
00309                     string targetUser = subtokens[0];
00310                     string message = subtokens[1];
00311                     this.chatClient.SendPrivateMessage(targetUser, message);
00312                 }
00313                 else if ((tokens[0].Equals("\\join") || tokens[0].Equals("\\j")) && !string.IsNullOrEmpty(tokens[1]))
00314                 {
00315                     string[] subtokens = tokens[1].Split(new char[] { ' ', ',' }, 2);
00316 
00317                     // If we are already subscribed to the channel we directly switch to it, otherwise we subscribe to it first and then switch to it implicitly
00318                     if (this.channelToggles.ContainsKey(subtokens[0]))
00319                     {
00320                         this.ShowChannel(subtokens[0]);
00321                     }
00322                     else
00323                     {
00324                         this.chatClient.Subscribe(new string[] { subtokens[0] });
00325                     }
00326                 }
00327                 else
00328                 {
00329                     Debug.Log("The command '" + tokens[0] + "' is invalid.");
00330                 }
00331             }
00332             else
00333             {
00334                 if (doingPrivateChat)
00335                 {
00336                     this.chatClient.SendPrivateMessage(privateChatTarget, inputLine);
00337                 }
00338                 else
00339                 {
00340                     this.chatClient.PublishMessage(this.selectedChannelName, inputLine);
00341                 }
00342             }
00343         }
