00366         public void OnConnected()
00367         {
00368             if (this.ChannelsToJoinOnConnect != null && this.ChannelsToJoinOnConnect.Length > 0)
00369             {
00370                 this.chatClient.Subscribe(this.ChannelsToJoinOnConnect, this.HistoryLengthToFetch);
00371             }
00372 
00373             this.ConnectingLabel.SetActive(false);
00374 
00375             this.UserIdText.text = "Connected as "+ this.UserName;
00376 
00377             this.ChatPanel.gameObject.SetActive(true);
00378 
00379             if (this.FriendsList!=null  && this.FriendsList.Length>0)
00380             {
00381                 this.chatClient.AddFriends(this.FriendsList); // Add some users to the server-list to get their status updates
00382 
00383                 // add to the UI as well
00384                 foreach(string _friend in this.FriendsList)
00385                 {
00386                     if (this.FriendListUiItemtoInstantiate != null && _friend!= this.UserName)
00387                     {
00388                         this.InstantiateFriendButton(_friend);
00389                     }
00390 
00391                 }
00392 
00393             }
00394 
00395             if (this.FriendListUiItemtoInstantiate != null)
00396             {
00397                 this.FriendListUiItemtoInstantiate.SetActive(false);
00398             }
00399 
00400 
00401             this.chatClient.SetOnlineStatus(ChatUserStatus.Online); // You can set your online state (without a mesage).
00402         }
