00493         public void OnUnsubscribed(string[] channels)
00494         {
00495             foreach (string channelName in channels)
00496             {
00497                 if (this.channelToggles.ContainsKey(channelName))
00498                 {
00499                     Toggle t = this.channelToggles[channelName];
00500                     Destroy(t.gameObject);
00501 
00502                     this.channelToggles.Remove(channelName);
00503 
00504                     Debug.Log("Unsubscribed from channel '" + channelName + "'.");
00505 
00506                     // Showing another channel if the active channel is the one we unsubscribed from before
00507                     if (channelName == this.selectedChannelName && this.channelToggles.Count > 0)
00508                     {
00509                         IEnumerator<KeyValuePair<string, Toggle>> firstEntry = this.channelToggles.GetEnumerator();
00510                         firstEntry.MoveNext();
00511 
00512                         this.ShowChannel(firstEntry.Current.Key);
00513 
00514                         firstEntry.Current.Value.isOn = true;
00515                     }
00516                 }
00517                 else
00518                 {
00519                     Debug.Log("Can't unsubscribe from channel '" + channelName + "' because you are currently not subscribed to it.");
00520                 }
00521             }
00522         }
