00615         public void ShowChannel(string channelName)
00616         {
00617             if (string.IsNullOrEmpty(channelName))
00618             {
00619                 return;
00620             }
00621 
00622             ChatChannel channel = null;
00623             bool found = this.chatClient.TryGetChannel(channelName, out channel);
00624             if (!found)
00625             {
00626                 Debug.Log("ShowChannel failed to find channel: " + channelName);
00627                 return;
00628             }
00629 
00630             this.selectedChannelName = channelName;
00631             this.CurrentChannelText.text = channel.ToStringMessages();
00632             Debug.Log("ShowChannel: " + this.selectedChannelName);
00633 
00634             foreach (KeyValuePair<string, Toggle> pair in this.channelToggles)
00635             {
00636                 pair.Value.isOn = pair.Key == channelName ? true : false;
00637             }
00638         }
