00103         private string GetNameServerAddress()
00104         {
00105             var protocolPort = 0;
00106             ProtocolToNameServerPort.TryGetValue(this.TransportProtocol, out protocolPort);
00107 
00108             if (this.NameServerPortOverride != 0)
00109             {
00110                 this.Listener.DebugReturn(DebugLevel.INFO, string.Format("Using NameServerPortInAppSettings as port for Name Server: {0}", this.NameServerPortOverride));
00111                 protocolPort = this.NameServerPortOverride;
00112             }
00113 
00114             switch (this.TransportProtocol)
00115             {
00116                 case ConnectionProtocol.Udp:
00117                 case ConnectionProtocol.Tcp:
00118                     return string.Format("{0}:{1}", NameServerHost, protocolPort);
00119                 case ConnectionProtocol.WebSocket:
00120                     return string.Format("ws://{0}:{1}", NameServerHost, protocolPort);
00121                 case ConnectionProtocol.WebSocketSecure:
00122                     return string.Format("wss://{0}:{1}", NameServerHost, protocolPort);
00123                 default:
00124                     throw new ArgumentOutOfRangeException();
00125             }
00126         }
