00142         public bool AuthenticateOnNameServer(string appId, string appVersion, string region, AuthenticationValues authValues)
00143         {
00144             if (this.DebugOut >= DebugLevel.INFO)
00145             {
00146                 this.Listener.DebugReturn(DebugLevel.INFO, "OpAuthenticate()");
00147             }
00148 
00149             var opParameters = new Dictionary<byte, object>();
00150 
00151             opParameters[ParameterCode.AppVersion] = appVersion;
00152             opParameters[ParameterCode.ApplicationId] = appId;
00153             opParameters[ParameterCode.Region] = region;
00154 
00155             if (authValues != null)
00156             {
00157                 if (!string.IsNullOrEmpty(authValues.UserId))
00158                 {
00159                     opParameters[ParameterCode.UserId] = authValues.UserId;
00160                 }
00161 
00162                 if (authValues.AuthType != CustomAuthenticationType.None)
00163                 {
00164                     opParameters[ParameterCode.ClientAuthenticationType] = (byte) authValues.AuthType;
00165                     if (authValues.Token != null)
00166                     {
00167                         opParameters[ParameterCode.Secret] = authValues.Token;
00168                     }
00169                     else
00170                     {
00171                         if (!string.IsNullOrEmpty(authValues.AuthGetParameters))
00172                         {
00173                             opParameters[ParameterCode.ClientAuthenticationParams] = authValues.AuthGetParameters;
00174                         }
00175                         if (authValues.AuthPostData != null)
00176                         {
00177                             opParameters[ParameterCode.ClientAuthenticationData] = authValues.AuthPostData;
00178                         }
00179                     }
00180                 }
00181             }
00182 
00183             return this.SendOperation(ChatOperationCode.Authenticate, opParameters, new SendOptions() { Reliability = true, Encrypt = this.IsEncryptionAvailable });
00184         }
