00687 
00688 
00689 
00690         //Put back zone
00691         if (objectCol.gameObject.CompareTag("Broom_Zone") && havePenyapu)
00692         {
00693             inBroomZone = false;
00694             choreButton.interactable = false;
00695 
00696             Debug.Log("Player_Human is NOT in broom zone.");
00697         }
00698         if (objectCol.gameObject.CompareTag("Duster_Zone") && haveDuster)
00699         {
00700             inDusterZone = false;
00701             choreButton.interactable = false;
00702 
00703             Debug.Log("Player_Human is NOT in duster zone.");
00704         }
00705 
00706         if (objectCol.gameObject.CompareTag("Cloth_Zone") && haveCloth)
00707         {
00708             inClothZone = false;
00709             choreButton.interactable = false;
00710 
00711             Debug.Log("Player_Human is NOT in cloth zone.");
00712         }
00713 
00714         if (objectCol.gameObject.CompareTag("Watercan_Zone") && haveWatercan)
00715         {
00716             inWatercanZone = false;
00717             choreButton.interactable = false;
00718 
00719             Debug.Log("Player_Human is NOT in watercan zone.");
00720         }
00721 
00722         if (objectCol.gameObject.CompareTag("Defrost_Zone") && haveChicken)
00723         {
00724             inDefrostZone = false;
00725             choreButton.interactable = false;
00726 
00727             Debug.Log("Player_Human is NOT in defrost zone.");
00728         }
00729 
00730     }
