00072         public static Project CreateCloneFromPath(string sourceProjectPath)
00073         {
00074             Project sourceProject = new Project(sourceProjectPath);
00075 
00076             string cloneProjectPath = null;
00077 
00078             //Find available clone suffix id
00079             for (int i = 0; i < MaxCloneProjectCount; i++)
00080             {
00081                 string originalProjectPath = ClonesManager.GetCurrentProject().projectPath;
00082                 string possibleCloneProjectPath = originalProjectPath + ClonesManager.CloneNameSuffix + "_" + i;
00083 
00084                 if (!Directory.Exists(possibleCloneProjectPath))
00085                 {
00086                     cloneProjectPath = possibleCloneProjectPath;
00087                     break;
00088                 }
00089             }
00090 
00091             if (string.IsNullOrEmpty(cloneProjectPath))
00092             {
00093                 Debug.LogError("The number of cloned projects has reach its limit. Limit: " + MaxCloneProjectCount);
00094                 return null;
00095             }
00096 
00097             Project cloneProject = new Project(cloneProjectPath);
00098 
00099             Debug.Log("Start cloning project, original project: " + sourceProject + ", clone project: " + cloneProject);
00100 
00101             ClonesManager.CreateProjectFolder(cloneProject);
00102 
00103             //Copy Folders           
00104             Debug.Log("Library copy: " + cloneProject.libraryPath);
00105             ClonesManager.CopyDirectoryWithProgressBar(sourceProject.libraryPath, cloneProject.libraryPath,
00106                 "Cloning Project Library '" + sourceProject.name + "'. ");
00107             Debug.Log("Packages copy: " + cloneProject.libraryPath);
00108             ClonesManager.CopyDirectoryWithProgressBar(sourceProject.packagesPath, cloneProject.packagesPath,
00109               "Cloning Project Packages '" + sourceProject.name + "'. ");
00110 
00111 
00112             //Link Folders
00113             ClonesManager.LinkFolders(sourceProject.assetPath, cloneProject.assetPath);
00114             ClonesManager.LinkFolders(sourceProject.projectSettingsPath, cloneProject.projectSettingsPath);
00115             ClonesManager.LinkFolders(sourceProject.autoBuildPath, cloneProject.autoBuildPath);
00116             ClonesManager.LinkFolders(sourceProject.localPackages, cloneProject.localPackages);
00117 
00118             ClonesManager.RegisterClone(cloneProject);
00119 
00120             return cloneProject;
00121         }
