00190         public static bool IsCloneProjectRunning(string projectPath)
00191         {
00192 
00193             //Determine whether it is opened in another instance by checking the UnityLockFile
00194             string UnityLockFilePath = new string[] { projectPath, "Temp", "UnityLockfile" }
00195                 .Aggregate(Path.Combine);
00196 
00197             switch (Application.platform)
00198             {
00199                 case (RuntimePlatform.WindowsEditor):
00200                     //Windows editor will lock "UnityLockfile" file when project is being opened.
00201                     //Sometime, for instance: windows editor crash, the "UnityLockfile" will not be deleted even the project
00202                     //isn't being opened, so a check to the "UnityLockfile" lock status may be necessary.
00203                     if (Preferences.AlsoCheckUnityLockFileStaPref.Value)
00204                         return File.Exists(UnityLockFilePath) && FileUtilities.IsFileLocked(UnityLockFilePath);
00205                     else
00206                         return File.Exists(UnityLockFilePath);
00207                 case (RuntimePlatform.OSXEditor):
00208                     //Mac editor won't lock "UnityLockfile" file when project is being opened
00209                     return File.Exists(UnityLockFilePath);
00210                 case (RuntimePlatform.LinuxEditor):
00211                     return File.Exists(UnityLockFilePath);
00212                 default:
00213                     throw new System.NotImplementedException("IsCloneProjectRunning: Unsupport Platfrom: " + Application.platform);
00214             }
00215         }
