00220         public static void DeleteClone(string cloneProjectPath)
00221         {
00223             if (ClonesManager.IsClone()) return;
00224 
00226             if (cloneProjectPath == string.Empty) return;
00227             if (cloneProjectPath == ClonesManager.GetOriginalProjectPath()) return;
00228 
00229             //Check what OS is
00230             string identifierFile;
00231             string args;
00232             switch (Application.platform)
00233             {
00234                 case (RuntimePlatform.WindowsEditor):
00235                     Debug.Log("Attempting to delete folder \"" + cloneProjectPath + "\"");
00236 
00237                     //The argument file will be deleted first at the beginning of the project deletion process 
00238                     //to prevent any further reading and writing to it(There's a File.Exist() check at the (file)editor windows.)
00239                     //If there's any file in the directory being write/read during the deletion process, the directory can't be fully removed.
00240                     identifierFile = Path.Combine(cloneProjectPath, ClonesManager.ArgumentFileName);
00241                     File.Delete(identifierFile);
00242 
00243                     args = "/c " + @"rmdir /s/q " + string.Format("\"{0}\"", cloneProjectPath);
00244                     StartHiddenConsoleProcess("cmd.exe", args);
00245 
00246                     break;
00247                 case (RuntimePlatform.OSXEditor):
00248                     Debug.Log("Attempting to delete folder \"" + cloneProjectPath + "\"");
00249 
00250                     //The argument file will be deleted first at the beginning of the project deletion process 
00251                     //to prevent any further reading and writing to it(There's a File.Exist() check at the (file)editor windows.)
00252                     //If there's any file in the directory being write/read during the deletion process, the directory can't be fully removed.
00253                     identifierFile = Path.Combine(cloneProjectPath, ClonesManager.ArgumentFileName);
00254                     File.Delete(identifierFile);
00255 
00256                     FileUtil.DeleteFileOrDirectory(cloneProjectPath);
00257 
00258                     break;
00259                 case (RuntimePlatform.LinuxEditor):
00260                     Debug.Log("Attempting to delete folder \"" + cloneProjectPath + "\"");
00261                     identifierFile = Path.Combine(cloneProjectPath, ClonesManager.ArgumentFileName);
00262                     File.Delete(identifierFile);
00263 
00264                     FileUtil.DeleteFileOrDirectory(cloneProjectPath);
00265 
00266                     break;
00267                 default:
00268                     Debug.LogWarning("Not in a known editor. Where are you!?");
00269                     break;
00270             }
00271         }
