00382                         break;
00383                     default:
00384                         Debug.LogWarning("Not in a known editor. Application.platform: " + Application.platform);
00385                         break;
00386                 }
00387             }
00388             else
00389             {
00390                 Debug.LogWarning("Skipping Asset link, it already exists: " + destinationPath);
00391             }
