00533         private static void CopyDirectoryWithProgressBarRecursive(DirectoryInfo source, DirectoryInfo destination,
00534             ref long totalBytes, ref long copiedBytes, string progressBarPrefix = "")
00535         {
00537             if (source.FullName.ToLower() == destination.FullName.ToLower())
00538             {
00539                 Debug.LogError("Cannot copy directory into itself.");
00540                 return;
00541             }
00542 
00544             if (totalBytes == 0)
00545             {
00546                 totalBytes = ClonesManager.GetDirectorySize(source, true, progressBarPrefix);
00547             }
00548 
00550             if (!Directory.Exists(destination.FullName))
00551             {
00552                 Directory.CreateDirectory(destination.FullName);
00553             }
00554 
00556             foreach (FileInfo file in source.GetFiles())
00557             {
00558                 try
00559                 {
00560                     file.CopyTo(Path.Combine(destination.ToString(), file.Name), true);
00561                 }
00562                 catch (IOException)
00563                 {
00566                 }
00567 
00569                 copiedBytes += file.Length;
00570 
00572                 float progress = (float)copiedBytes / (float)totalBytes;
00573                 bool cancelCopy = EditorUtility.DisplayCancelableProgressBar(
00574                     progressBarPrefix + "Copying '" + source.FullName + "' to '" + destination.FullName + "'...",
00575                     "(" + (progress * 100f).ToString("F2") + "%) Copying file '" + file.Name + "'...",
00576                     progress);
00577                 if (cancelCopy) return;
00578             }
00579 
00581             foreach (DirectoryInfo sourceNestedDir in source.GetDirectories())
00582             {
00583                 DirectoryInfo nextDestingationNestedDir = destination.CreateSubdirectory(sourceNestedDir.Name);
00584                 ClonesManager.CopyDirectoryWithProgressBarRecursive(sourceNestedDir, nextDestingationNestedDir,
00585                     ref totalBytes, ref copiedBytes, progressBarPrefix);
00586             }
00587         }
