00596         private static long GetDirectorySize(DirectoryInfo directory, bool includeNested = false,
00597             string progressBarPrefix = "")
00598         {
00599             EditorUtility.DisplayProgressBar(progressBarPrefix + "Calculating size of directories...",
00600                 "Scanning '" + directory.FullName + "'...", 0f);
00601 
00603             long filesSize = directory.GetFiles().Sum((FileInfo file) => file.Length);
00604 
00606             long directoriesSize = 0;
00607             if (includeNested)
00608             {
00609                 IEnumerable<DirectoryInfo> nestedDirectories = directory.GetDirectories();
00610                 foreach (DirectoryInfo nestedDir in nestedDirectories)
00611                 {
00612                     directoriesSize += ClonesManager.GetDirectorySize(nestedDir, true, progressBarPrefix);
00613                 }
00614             }
00615 
00616             return filesSize + directoriesSize;
00617         }
