00633         private static void ExecuteBashCommand(string command)
00634         {
00635             command = command.Replace("\"", "\"\"");
00636 
00637             var proc = new Process()
00638             {
00639                 StartInfo = new ProcessStartInfo
00640                 {
00641                     FileName = "/bin/bash",
00642                     Arguments = "-c \"" + command + "\"",
00643                     UseShellExecute = false,
00644                     RedirectStandardOutput = true,
00645                     RedirectStandardError = true,
00646                     CreateNoWindow = true
00647                 }
00648             };
00649 
00650             using (proc)
00651             {
00652                 proc.Start();
00653                 proc.WaitForExit();
00654 
00655                 if (!proc.StandardError.EndOfStream)
00656                 {
00657                     UnityEngine.Debug.LogError(proc.StandardError.ReadToEnd());
00658                 }
00659             }
00660         }
