00024         public int ConvertFrameLocalToOrigin(int localFrameId)
00025         {
00026             int frameCount = TickEngineSettings.frameCount;
00027             int originFrameId = localFrameId + localToOriginFrame;
00028             if (originFrameId >= frameCount)
00029                 originFrameId -= frameCount;
00030             return originFrameId;
00031         }
00032 
00033         public int ConvertFrameOriginToLocal(int originFrameId)
00034         {
00035             int frameCount = TickEngineSettings.frameCount;
00036             int localFrameId = originFrameId + originToLocalFrame;
00037             if (localFrameId >= frameCount)
00038                 localFrameId -= frameCount;
00039             return localFrameId;
00040         }
