00067         public void SnapshotAdvance()
00068         {
00069             int frameCount = TickEngineSettings.frameCount;
00070 
00071             int currFrameId = NetMaster.CurrentFrameId;
00072 
00073             int origFrameId = currFrameId + localToOriginFrame;
00074             if (origFrameId >= frameCount)
00075                 origFrameId -= frameCount;
00076 
00078             int validCount = validFrameMask.CountValidRange(origFrameId, TickEngineSettings.quaterFrameCount);
00079 
00080             if (!hadInitialSnapshot)
00081             {
00082 
00083                 if (validCount == 0)
00084                 {
00085                     advanceCount = 0;
00086                     return;
00087                 }
00088                 else if (validCount > TickEngineSettings.targetBufferSize)
00089                 {
00090                     advanceCount = validCount - TickEngineSettings.targetBufferSize;
00091                     //Debug.Log("Setting advanceCount " + advanceCount);
00092                     return;
00093                 }
00094             }
00095 
00097             if (validCount == 0)
00098             {
00100                 if (frameArrivedTooLate)
00101                 {
00102                     numOfSequentialFramesWithTooLargeBuffer = 0;
00103                     numOfSequentialFramesWithTooSmallBuffer = 0;
00104                     frameArrivedTooLate = false;
00105 
00106 #if SNS_WARNINGS && UNITY_EDITOR
00107                     Debug.LogWarning(Time.time + " currlcl: " + currFrameId + " currorig: " + origFrameId + " <b><color=red>Frame arrived late with empty buffer</color> - HOLD</b> " + numOfSequentialFramesWithTooSmallBuffer + "/" + TickEngineSettings.ticksBeforeGrow);
00108 #endif
00109                     advanceCount = 0;
00110                     return;
00111                 
00112                 }
00114                 else
00115                 {
00116                     numOfSequentialFramesWithTooLargeBuffer = 0;
00117                     advanceCount = 1;
00118                 }
00119 
00120             }
00122             else if (validCount < TickEngineSettings.minBufferSize)
00123             {
00124                 numOfSequentialFramesWithTooLargeBuffer = 0;
00125                 numOfSequentialFramesWithTooSmallBuffer += (frameArrivedTooLate ? 2 : 1);
00126                 frameArrivedTooLate = false;
00127 
00128                 if (numOfSequentialFramesWithTooSmallBuffer >= TickEngineSettings.ticksBeforeGrow)
00129                 {
00130 #if SNS_WARNINGS && UNITY_EDITOR
00131                     Debug.LogWarning(Time.time + " <b>Buffer Low</b> - <b>HOLD</b>  conn: " + connId + " fid: " + currFrameId + " buffsze: " + validCount);
00132 #endif
00133                     advanceCount = 0;
00134                     return;
00135                 }
00136                 else
00137                     advanceCount = 1;
00138             }
00140             else if (validCount > TickEngineSettings.maxBufferSize)
00141             {
00142                 numOfSequentialFramesWithTooSmallBuffer = 0;
00143                 if (numOfSequentialFramesWithTooLargeBuffer > TickEngineSettings.ticksBeforeGrow)
00144                 {
00145 
00147                     advanceCount = (validCount - TickEngineSettings.targetBufferSize) + 1;
00148 
00149 #if SNS_WARNINGS && UNITY_EDITOR
00150                     Debug.LogWarning(Time.time + " <b>SKIP  </b>Trimming Oversized Buffer for Player: " + connId + " advance: " + advanceCount + " validCount: " + validCount
00151                         + " frameArrivedTooLate:" + frameArrivedTooLate);
00152 #endif
00153                     numOfSequentialFramesWithTooLargeBuffer = 0; // /= 2;
00154                 }
00155                 else
00156                 {
00157                     advanceCount = 1;
00158                     numOfSequentialFramesWithTooLargeBuffer++;
00159                 }
00160             }
00162             else
00163             {
00164                 numOfSequentialFramesWithTooLargeBuffer = 0;
00165                 numOfSequentialFramesWithTooSmallBuffer = (frameArrivedTooLate ? 1 : 0);
00166                 advanceCount = 1;
00167             }
00168 
00169             frameArrivedTooLate = false;
00170             return;
00171 
00172         }
