00174         public void PostSnapshot()
00175         {
00176             int frameCount = TickEngineSettings.frameCount;
00177             int currFrameId = NetMaster.CurrentFrameId;
00178 
00179             int origFrameId = currFrameId + localToOriginFrame;
00180             if (origFrameId >= frameCount)
00181                 origFrameId -= frameCount;
00182 
00183             int invalidate = origFrameId - (TickEngineSettings.quaterFrameCount);
00184             if (invalidate < 0)
00185                 invalidate += frameCount;
00186 
00189             validFrameMask.ClearBitsBefore(invalidate, TickEngineSettings.quaterFrameCount);
00190 
00191             if (advanceCount > 0)
00192                 hadInitialSnapshot = true;
00193 
00194             if (advanceCount != 1)
00195             {
00196                 localToOriginFrame += (advanceCount - 1);
00197                 if (localToOriginFrame < 0)
00198                     localToOriginFrame += frameCount;
00199                 else if (localToOriginFrame >= frameCount)
00200                     localToOriginFrame -= frameCount;
00201 
00202                 originToLocalFrame = frameCount - localToOriginFrame;
00203                 if (originToLocalFrame < 0)
00204                     originToLocalFrame += frameCount;
00205             }
00206         }
00207     }
