00116         private void CreateCellHierarchy()
00117         {
00118             if (!this.IsCellCountAllowed())
00119             {
00120                 if (Debug.isDebugBuild)
00121                 {
00122                     Debug.LogError("There are too many cells created by your subdivision options. Maximum allowed number of cells is " + (MAX_NUMBER_OF_ALLOWED_CELLS - this.FIRST_GROUP_ID) +
00123                                    ". Current number of cells is " + this.CellCount + ".");
00124                     return;
00125                 }
00126                 else
00127                 {
00128                     Application.Quit();
00129                 }
00130             }
00131 
00132             CellTreeNode rootNode = new CellTreeNode(this.idCounter++, CellTreeNode.ENodeType.Root, null);
00133 
00134             if (this.YIsUpAxis)
00135             {
00136                 this.Center = new Vector2(transform.position.x, transform.position.y);
00137                 this.Size = new Vector2(transform.localScale.x, transform.localScale.y);
00138 
00139                 rootNode.Center = new Vector3(this.Center.x, this.Center.y, 0.0f);
00140                 rootNode.Size = new Vector3(this.Size.x, this.Size.y, 0.0f);
00141                 rootNode.TopLeft = new Vector3((this.Center.x - (this.Size.x / 2.0f)), (this.Center.y - (this.Size.y / 2.0f)), 0.0f);
00142                 rootNode.BottomRight = new Vector3((this.Center.x + (this.Size.x / 2.0f)), (this.Center.y + (this.Size.y / 2.0f)), 0.0f);
00143             }
00144             else
00145             {
00146                 this.Center = new Vector2(transform.position.x, transform.position.z);
00147                 this.Size = new Vector2(transform.localScale.x, transform.localScale.z);
00148 
00149                 rootNode.Center = new Vector3(this.Center.x, 0.0f, this.Center.y);
00150                 rootNode.Size = new Vector3(this.Size.x, 0.0f, this.Size.y);
00151                 rootNode.TopLeft = new Vector3((this.Center.x - (this.Size.x / 2.0f)), 0.0f, (this.Center.y - (this.Size.y / 2.0f)));
00152                 rootNode.BottomRight = new Vector3((this.Center.x + (this.Size.x / 2.0f)), 0.0f, (this.Center.y + (this.Size.y / 2.0f)));
00153             }
00154 
00155             this.CreateChildCells(rootNode, 1);
00156 
00157             this.CellTree = new CellTree(rootNode);
00158 
00159             this.RecreateCellHierarchy = false;
00160         }
