00167         private void CreateChildCells(CellTreeNode parent, int cellLevelInHierarchy)
00168         {
00169             if (cellLevelInHierarchy > this.NumberOfSubdivisions)
00170             {
00171                 return;
00172             }
00173 
00174             int rowCount = (int)this.Subdivisions[(cellLevelInHierarchy - 1)].x;
00175             int columnCount = (int)this.Subdivisions[(cellLevelInHierarchy - 1)].y;
00176 
00177             float startX = parent.Center.x - (parent.Size.x / 2.0f);
00178             float width = parent.Size.x / rowCount;
00179 
00180             for (int row = 0; row < rowCount; ++row)
00181             {
00182                 for (int column = 0; column < columnCount; ++column)
00183                 {
00184                     float xPos = startX + (row * width) + (width / 2.0f);
00185 
00186                     CellTreeNode node = new CellTreeNode(this.idCounter++, (this.NumberOfSubdivisions == cellLevelInHierarchy) ? CellTreeNode.ENodeType.Leaf : CellTreeNode.ENodeType.Node, parent);
00187 
00188                     if (this.YIsUpAxis)
00189                     {
00190                         float startY = parent.Center.y - (parent.Size.y / 2.0f);
00191                         float height = parent.Size.y / columnCount;
00192                         float yPos = startY + (column * height) + (height / 2.0f);
00193 
00194                         node.Center = new Vector3(xPos, yPos, 0.0f);
00195                         node.Size = new Vector3(width, height, 0.0f);
00196                         node.TopLeft = new Vector3(xPos - (width / 2.0f), yPos - (height / 2.0f), 0.0f);
00197                         node.BottomRight = new Vector3(xPos + (width / 2.0f), yPos + (height / 2.0f), 0.0f);
00198                     }
00199                     else
00200                     {
00201                         float startZ = parent.Center.z - (parent.Size.z / 2.0f);
00202                         float depth = parent.Size.z / columnCount;
00203                         float zPos = startZ + (column * depth) + (depth / 2.0f);
00204 
00205                         node.Center = new Vector3(xPos, 0.0f, zPos);
00206                         node.Size = new Vector3(width, 0.0f, depth);
00207                         node.TopLeft = new Vector3(xPos - (width / 2.0f), 0.0f, zPos - (depth / 2.0f));
00208                         node.BottomRight = new Vector3(xPos + (width / 2.0f), 0.0f, zPos + (depth / 2.0f));
00209                     }
00210 
00211                     parent.AddChild(node);
00212 
00213                     this.CreateChildCells(node, (cellLevelInHierarchy + 1));
00214                 }
00215             }
00216         }
