00237         private bool IsCellCountAllowed()
00238         {
00239             int horizontalCells = 1;
00240             int verticalCells = 1;
00241 
00242             foreach (Vector2 v in this.Subdivisions)
00243             {
00244                 horizontalCells *= (int)v.x;
00245                 verticalCells *= (int)v.y;
00246             }
00247 
00248             this.CellCount = horizontalCells * verticalCells;
00249 
00250             return (this.CellCount <= (MAX_NUMBER_OF_ALLOWED_CELLS - this.FIRST_GROUP_ID));
00251         }
