00258         public List<byte> GetActiveCells(Vector3 position)
00259         {
00260             List<byte> activeCells = new List<byte>(0);
00261             this.CellTree.RootNode.GetActiveCells(activeCells, this.YIsUpAxis, position);
00262 
00263             // it makes sense to sort the "nearby" cells. those are in the list in positions after the subdivisions the point is inside. 2 subdivisions result in 3 areas the point is in.
00264             int cellsActive = this.NumberOfSubdivisions + 1;
00265             int cellsNearby = activeCells.Count - cellsActive;
00266             if (cellsNearby > 0)
00267             {
00268                 activeCells.Sort(cellsActive, cellsNearby, new ByteComparer());
00269             }
00270             return activeCells;
00271         }
