00411         public void GetActiveCells(List<byte> activeCells, bool yIsUpAxis, Vector3 position)
00412         {
00413             if (this.NodeType != ENodeType.Leaf)
00414             {
00415                 foreach (CellTreeNode node in this.Childs)
00416                 {
00417                     node.GetActiveCells(activeCells, yIsUpAxis, position);
00418                 }
00419             }
00420             else
00421             {
00422                 if (this.IsPointNearCell(yIsUpAxis, position))
00423                 {
00424                     if (this.IsPointInsideCell(yIsUpAxis, position))
00425                     {
00426                         activeCells.Insert(0, this.Id);
00427 
00428                         CellTreeNode p = this.Parent;
00429                         while (p != null)
00430                         {
00431                             activeCells.Insert(0, p.Id);
00432 
00433                             p = p.Parent;
00434                         }
00435                     }
00436                     else
00437                     {
00438                         activeCells.Add(this.Id);
00439                     }
00440                 }
00441             }
00442         }
