00450         public bool IsPointInsideCell(bool yIsUpAxis, Vector3 point)
00451         {
00452             if ((point.x < this.TopLeft.x) || (point.x > this.BottomRight.x))
00453             {
00454                 return false;
00455             }
00456 
00457             if (yIsUpAxis)
00458             {
00459                 if ((point.y >= this.TopLeft.y) && (point.y <= this.BottomRight.y))
00460                 {
00461                     return true;
00462                 }
00463             }
00464             else
00465             {
00466                 if ((point.z >= this.TopLeft.z) && (point.z <= this.BottomRight.z))
00467                 {
00468                     return true;
00469                 }
00470             }
00471 
00472             return false;
00473         }
00474 
00481         public bool IsPointNearCell(bool yIsUpAxis, Vector3 point)
00482         {
00483             if (this.maxDistance == 0.0f)
00484             {
00485                 this.maxDistance = (this.Size.x + this.Size.y + this.Size.z) / 2.0f;
00486             }
00487 
00488             return ((point - this.Center).sqrMagnitude <= (this.maxDistance * this.maxDistance));
00489         }
