00260         public void SelectDemo(string Reference)
00261         {
00262             currentSelection = Reference;
00263 
00264             TitleText.text = _data[currentSelection].Title;
00265             DescriptionText.text = _data[currentSelection].Description;
00266 
00267             OpenSceneButton.SetActive(!string.IsNullOrEmpty(_data[currentSelection].Scene));
00268 
00269             OpenTutorialLinkButton.SetActive(!string.IsNullOrEmpty(_data[currentSelection].TutorialLink));
00270             OpenDocLinkButton.SetActive(!string.IsNullOrEmpty(_data[currentSelection].DocLink));
00271         }
00272 
00273         public void OpenScene()
00274         {
00275             if (string.IsNullOrEmpty(currentSelection))
00276             {
00277                 Debug.LogError("Bad setup, a CurrentSelection is expected at this point");
00278                 return;
