00312         List<string> RecursiveSerch(string dir, string filePattern ,List<string> returnList){
00313             try 
00314             {
00315                 foreach (string d in Directory.GetDirectories(dir)) 
00316                 {
00317                     foreach (string f in Directory.GetFiles(d, filePattern)) 
00318                     {
00319                         returnList.Add(f);
00320                     }
00321                     returnList = RecursiveSerch(d,filePattern, returnList);
00322                 }
00323             }
00324             catch (System.Exception excpt) 
00325             {
00326                 Debug.Log(excpt.Message);
00327             }
00328             return returnList;
00329         }
