00080         public static List<GameObject> FindAllPrefabInstances(GameObject prefabParent)
00081         {
00082             //Debug.Log("Finding all instances of prefab '" + prefabParent.name +"' in scene");
00083             List<GameObject> result = new List<GameObject>();
00084             GameObject[] allObjects = GameObject.FindObjectsOfType<GameObject>();
00085             foreach (GameObject GO in allObjects)
00086             {
00087 #if UNITY_2018_2_OR_NEWER
00088                 var parPrefab = PrefabUtility.GetCorrespondingObjectFromSource(GO) as GameObject;
00089 #else
00090                 var parPrefab = PrefabUtility.GetPrefabParent(GO);
00091 #endif
00092 
00093 
00094                 if (parPrefab == prefabParent)
00095                 {
00096                     //Debug.Log("Found prefab instance: " + GO.name);
00097 
00098                     UnityEngine.Object GO_prefab = parPrefab;
00099                     if (prefabParent == GO_prefab)
00100                         result.Add(GO);
00101                 }
00102             }
00103             return result;
00104         }
