00109         public static T EnsureRootComponentExists<T>(this GameObject go, bool isExpanded = true) where T : Component
00110         {
00111 #if UNITY_2018_2_OR_NEWER
00112             GameObject parPrefab = PrefabUtility.GetCorrespondingObjectFromSource(go) as GameObject;
00113 #else
00114             GameObject parPrefab = PrefabUtility.GetPrefabParent(go) as GameObject;
00115 #endif
00116 
00117             T component;
00118 
00119             if (parPrefab)
00120             {
00121                 component = parPrefab.GetComponent<T>();
00122 
00123                 // Remove the NI from a scene object before we add it to the prefab
00124                 if (component == null)
00125                 {
00126                     List<GameObject> clones = FindAllPrefabInstances(parPrefab);
00127 
00128                     // Delete all instances of this root component in all instances of the prefab, so when we add to the prefab source they all get it - without repeats
00129                     foreach (GameObject clone in clones)
00130                     {
00131                         T[] comp = clone.GetComponents<T>();
00132                         foreach (T t in comp)
00133                         {
00134                             Debug.Log("Destroy " + t);
00135                             GameObject.DestroyImmediate(t);
00136 
00137                         }
00138                     }
00139                     component = parPrefab.AddComponent<T>();
00140                     UnityEditorInternal.InternalEditorUtility.SetIsInspectorExpanded(component, isExpanded);
00141                 }
00142             }
00143 
00144             // this is has no prefab source
00145             else
00146             {
00147                 component = go.GetComponent<T>();
00148                 if (!component)
00149                 {
00150                     component = go.AddComponent<T>();
00151                     UnityEditorInternal.InternalEditorUtility.SetIsInspectorExpanded(component, isExpanded);
00152                 }
00153             }
00154 
00155             return component;
00156         }
