00214         public static void DeleteComponentAtSource(this GameObject go, Type type)
00215         {
00216             if (type == null)
00217                 return;
00218 
00219 #if UNITY_2018_2_OR_NEWER
00220             GameObject parPrefab = PrefabUtility.GetCorrespondingObjectFromSource(go) as GameObject;
00221 #else
00222             GameObject parPrefab = PrefabUtility.GetPrefabParent(go) as GameObject;
00223 #endif
00224 
00225             if (parPrefab)
00226             {
00227                 List<GameObject> clones = FindAllPrefabInstances(parPrefab);
00228 
00229                 // Delete all instances of this root component in all instances of the prefab, so when we add to the prefab source they all get it - without repeats
00230                 foreach (GameObject clone in clones)
00231                 {
00232                     clone.GetComponents(type, components);
00233                     foreach (Component t in components)
00234                         GameObject.DestroyImmediate(t);
00235                 }
00236             }
00237 
00238             // this is has no prefab source
00239             else
00240             {
00241                 go.GetComponents(type, components);
00242                 foreach (Component t in components)
00243                     GameObject.DestroyImmediate(t);
00244             }
00245 
00246         }
