00274 
00275             for (int i = 0; i < cnt; i++)
00276             {
00277                 EditorGUILayout.BeginHorizontal(GUILayout.Height(18));
00278 
00279                 // Add [+] button
00280                 var addbtnrect = EditorGUILayout.GetControlRect(GUILayout.MaxWidth(BTTN_WIDTH));
00281                 if (lockZero && i != 0)
00282                 {
00283                     EditorGUI.BeginDisabledGroup(cnt >= maxCount);
00284                     if (GUI.Button(addbtnrect, "+", (GUIStyle)"minibutton"))
00285                         add = i;
00286                     EditorGUI.EndDisabledGroup();
00287                 }
00288 
00289                 // Label
00290                 EditorGUI.BeginDisabledGroup(lockZero && i == 0);
00291                 string label = labelPrefix == null ? "[" + i + "]" : labelPrefix + " " + i;
00292                 EditorGUILayout.LabelField(label, GUILayout.MaxWidth(labelwidth));
00293                 EditorGUI.EndDisabledGroup();
00294 
00295                 // Field
00296                 SerializedProperty item = listSP.GetArrayElementAtIndex(i);
00297 
00298                 var fieldrect = EditorGUILayout.GetControlRect(GUILayout.MinWidth(64));
00299                 if (lockZero && i == 0)
00300                 {
00301                     EditorGUI.BeginDisabledGroup(true);
00302                     EditorGUI.LabelField(fieldrect, item.stringValue);
00303                     EditorGUI.EndDisabledGroup();
00304                 }
00305                 else
00306                 {
00307                     if (item.propertyType == SerializedPropertyType.String)
00308                         item.stringValue = EditorGUI.DelayedTextField(fieldrect, GUIContent.none, item.stringValue);
00309                     else
00310                         EditorGUILayout.PropertyField(item);
00311                     
00312                     // Delete [x] Button
00313                     var delbuttonrect = EditorGUILayout.GetControlRect(GUILayout.MaxWidth(BTTN_WIDTH));
00314                     if (GUI.Button(delbuttonrect, "X", (GUIStyle)"minibutton"))
00315                         delete = i;
00316                 }
00317 
00318                 EditorGUILayout.EndHorizontal();
00319             }
00320             // space
00321             EditorGUILayout.GetControlRect(false, 4);
00322 
00323             // Bottom [add] Button
00324             EditorGUI.BeginDisabledGroup(cnt >= maxCount);
00325             if (GUI.Button(EditorGUILayout.GetControlRect(false, 20), labelPrefix != null ? "Add " + labelPrefix : "Add"))
00326                 add = cnt;
00327             EditorGUI.EndDisabledGroup();
00328 
00329             if (add != -1)
00330             {
00331                 listSP.InsertArrayElementAtIndex(add);
00332 
00333                 if (forceUnique && !(listSP.propertyType == SerializedPropertyType.String))
00334                     EnforceUnique(listSP, add);
00335                 
00336             }
00337 
00338             if (delete != -1)
00339                 listSP.DeleteArrayElementAtIndex(delete);
00340             
00341             EditorGUILayout.EndVertical();
00342 
00343             if (EditorGUI.EndChangeCheck())
00344             {
00345                 if (forceUnique && !(listSP.propertyType == SerializedPropertyType.String))
00346                     EnforceUnique(listSP);
00347 
00348                 listSP.serializedObject.ApplyModifiedProperties();
00349             }
00350         }
