00354         private static void EnforceUnique(SerializedProperty listSP, int indexToCheck)
00355         {
00356             int cnt = listSP.arraySize;
00357             var itemSP = listSP.GetArrayElementAtIndex(indexToCheck);
00358 
00359             bool failed;
00360             do
00361             {
00362                 var itemString = itemSP.stringValue;
00363                 failed = false;
00364 
00365                 for (int i = 0; i < cnt; i++)
00366                 {
00368                     if (i == indexToCheck)
00369                         continue;
00370 
00372                     if (listSP.GetArrayElementAtIndex(i).stringValue == itemString)
00373                     {
00374                         itemSP.stringValue += "X";
00375                         failed = true;
00376                         break;
00377                     }
00378                 }
00379             } while (failed);
00380         }
00381 
00382 
00383         private static void EnforceUnique(SerializedProperty listSP)
00384         {
00385             for (int i = 0, cnt = listSP.arraySize; i < cnt; i++)
00386             {
00387                 var item = listSP.GetArrayElementAtIndex(i);
00388                 while (IsTagAlreadyUsed(listSP, item.stringValue, i))
00389                     item.stringValue += "X";
00390             }
00391         }
00392 
00393 
00394         private static bool IsTagAlreadyUsed(SerializedProperty listSP, string tag, int countUpTo)
00395         {
00396             for (int i = 0; i < countUpTo; i++)
00397                 if (listSP.GetArrayElementAtIndex(i).stringValue == tag)
00398                     return true;
00399 
00400             return false;
00401         }
