00274     private void ClearPackageCache() {
00275         string path = string.Empty;
00276         if (Application.platform == RuntimePlatform.OSXEditor) {
00277             path = "~/Library/Unity/Asset Store-5.x/Dustyroom/";
00278         }
00279 
00280         if (Application.platform == RuntimePlatform.LinuxEditor) {
00281             path = "~/.local/share/unity3d/Asset Store-5.x/Dustyroom/";
00282         }
00283 
00284         if (Application.platform == RuntimePlatform.WindowsEditor) {
00285             // This wouldn't understand %APPDATA%.
00286             path = Application.persistentDataPath.Substring(0,
00287                        Application.persistentDataPath.IndexOf("AppData", StringComparison.Ordinal)) +
00288                    "/AppData/Roaming/Unity/Asset Store-5.x/Dustyroom";
00289         }
00290 
00291         if (path == string.Empty) return;
00292 
00293         _cacheClearedSuccessfully |= FileUtil.DeleteFileOrDirectory(path);
00294         _showingClearCacheMessage = true;
00295     }
