00331     private void ConfigureUrp() {
00332         string path = AssetDatabase.GUIDToAssetPath(UrpPipelineAssetGuid.ToString());
00333         if (path == null) {
00334             Debug.LogError($"[{AssetName}] Couldn't find the URP pipeline asset. " +
00335                            "Have you unpacked the URP package?");
00336             return;
00337         }
00338 
00339         var pipelineAsset = AssetDatabase.LoadAssetAtPath<RenderPipelineAsset>(path);
00340         if (pipelineAsset == null) {
00341             Debug.LogError($"[{AssetName}] Couldn't load the URP pipeline asset.");
00342             return;
00343         }
00344 
00345         Debug.Log(
00346             $"<b>[{AssetName}]</b> Set the render pipeline asset in the Graphics settings to the {AssetName} example.");
00347         GraphicsSettings.renderPipelineAsset = pipelineAsset;
00348         GraphicsSettings.defaultRenderPipeline = pipelineAsset;
00349 
00350         ChangePipelineAssetAllQualityLevels(pipelineAsset);
00351     }
00352 
00353     private void ConfigureBuiltIn() {
