00360     private void ChangePipelineAssetAllQualityLevels(RenderPipelineAsset pipelineAsset) {
00361         var originalQualityLevel = QualitySettings.GetQualityLevel();
00362 
00363         var logString = $"<b>[{AssetName}]</b> Set the render pipeline asset for the quality levels:";
00364 
00365         for (int i = 0; i < QualitySettings.names.Length; i++) {
00366             logString += $"\n\t{QualitySettings.names[i]}";
00367             QualitySettings.SetQualityLevel(i, false);
00368             QualitySettings.renderPipeline = pipelineAsset;
00369         }
00370 
00371         Debug.Log(logString);
00372 
00373         QualitySettings.SetQualityLevel(originalQualityLevel, false);
00374     }
