00471         private void DrawRotationRanges()
00472         {
00473             float sliderMax = (fc.axis == Axis.X && fc.UseHalfRangeX) ? 90 : 360;
00474             float sliderMin = (fc.axis == Axis.X && fc.UseHalfRangeX) ? -90 : -360;
00475 
00476             var minmax = DrawMinMaxSlider(fc.Min, fc.Max, sliderMin, sliderMax);
00477 
00478             float usedRange = Math.Min(minmax.max, sliderMax) - Math.Max(minmax.min, sliderMin);
00479 
00480             if (usedRange > 360)
00481                 minmax.max = Mathf.Min(minmax.min + 360, 360);
00482 
00483             if (fc.Min != minmax.min || fc.Min < sliderMin)
00484             {
00485                 haschanged = true;
00486                 Undo.RecordObject(p.serializedObject.targetObject, "Min value change");
00487                 fc.Min = Mathf.Max((int)minmax.min, sliderMin);
00488             }
00489 
00490             if (fc.Max != minmax.max || fc.Max > sliderMax)
00491             {
00492                 haschanged = true;
00493                 Undo.RecordObject(p.serializedObject.targetObject, "Max value change");
00494                 fc.Max = Mathf.Min((int)minmax.max, sliderMax);
00495             }
00496         }
